// Enhanced classification logic with binary data preservation
const items = $input.all();
const processedItems = [];

function extractFilename(data) {
  if (data.converted_pdf) return data.converted_pdf.split(/[\/]/).pop();
  if (data.converted_docx) return data.converted_docx.split(/[\/]/).pop();
  if (data.original_filename && data.original_filename !== 'undefined' && data.original_filename !== 'null') return data.original_filename;
  if (data.fileName) return data.fileName;
  if (data.filename) return data.filename;
  if (data.name) return data.name;
  if (Array.isArray(data.file) && data.file.length > 0 && data.file[0].filename) return data.file[0].filename;
  if (data.document_id) return `document_${data.document_id}.pdf`;
  return 'document_unknown.pdf';
}

function preserveBinaryData(item) {
  // Preserve all binary data properties
  if (item.binary) {
    return { ...item.binary };
  }
  return null;
}

function detectType(filename, mimetype) {
  const ext = filename.split('.').pop()?.toLowerCase();
  // Prefer mimetype if it is very specific
  if (mimetype) {
    const mt = mimetype.toLowerCase();
    if (mt.includes('officedocument.wordprocessingml.document')) return 'docx';
    if (mt.includes('msword')) {
      // Some browsers send .docx as msword, so check extension
      if (ext === 'docx') return 'docx';
      return 'doc';
    }
    if (mt.includes('pdf')) return 'pdf';
    if (mt.includes('image')) return 'image';
  }
  // Fallback to extension
  if (ext === 'docx') return 'docx';
  if (ext === 'doc') return 'doc';
  if (ext === 'pdf') return 'pdf';
  if (['jpg','jpeg','png'].includes(ext)) return 'image';
  return 'unknown';
}

for (const item of items) {
  const data = item.json;
  const documentId = data.document_id;
  const mimetype = data.mimeType || data.mimetype || (Array.isArray(data.file) && data.file.length > 0 && data.file[0].mimetype) || undefined;
  const preferredFilename = extractFilename(data);
  const detectedType = detectType(preferredFilename, mimetype);
  const qualityScore = data.quality_score || 0;
  const confidence = data.confidence || 0;
  let classificationFactors = [];
  let processingRequirements = [];
  if (detectedType === 'pdf') {
    classificationFactors.push('PDF document detected');
    processingRequirements.push('OCR processing', 'Text extraction');
  } else if (detectedType === 'docx') {
    classificationFactors.push('DOCX document detected');
    processingRequirements.push('Direct text extraction', 'Format parsing');
  } else if (detectedType === 'doc') {
    classificationFactors.push('DOC document detected');
    processingRequirements.push('Convert to DOCX', 'Direct text extraction');
  } else if (detectedType === 'image') {
    classificationFactors.push('Image document detected');
    processingRequirements.push('OCR processing', 'Image enhancement');
  } else {
    classificationFactors.push('Unknown document type');
    processingRequirements.push('Manual review');
  }
  if (qualityScore >= 0.8) {
    classificationFactors.push('High quality document');
    processingRequirements.push('Standard processing');
  } else if (qualityScore >= 0.5) {
    classificationFactors.push('Medium quality document');
    processingRequirements.push('Enhanced processing', 'Quality improvement');
  } else {
    classificationFactors.push('Low quality document');
    processingRequirements.push('Manual review', 'Quality enhancement');
  }
  const filename = preferredFilename.toLowerCase();
  let subType = 'general';
  if (filename.includes('cv') || filename.includes('resume')) {
    classificationFactors.push('Resume/CV document identified');
    processingRequirements.push('Personal info extraction', 'Skills parsing');
    subType = 'resume';
  } else if (filename.includes('application')) {
    classificationFactors.push('Application form identified');
    processingRequirements.push('Form field extraction', 'Structured data parsing');
    subType = 'application_form';
  } else if (filename.includes('certificate')) {
    subType = 'certificate';
  }
  processedItems.push({
    json: {
      document_id: documentId,
      document_type: detectedType,
      sub_type: subType,
      confidence: confidence,
      classification_factors: classificationFactors,
      processing_requirements: processingRequirements,
      original_filename: preferredFilename,
      quality_score: qualityScore,
      classification_metadata: {
        source_filename: preferredFilename,
        detected_extension: preferredFilename.split('.').pop()?.toLowerCase(),
        original_type: data.document_type || 'unknown',
        final_type: detectedType,
        mimetype: mimetype
      }
    }
  });
}

return processedItems;

// Enhanced file type classification with better detection
const items = $input.all();
const processedItems = [];

function detectFormat(documentType, filename, mimetype) {
  // Priority 1: Use backend classification if reliable
  if (documentType && documentType !== 'unknown') {
    return documentType;
  }

  // Priority 2: MIME type analysis
  if (mimetype) {
    const mimeType = mimetype.toLowerCase();
    if (mimeType.includes('pdf')) return 'pdf';
    if (mimeType.includes('msword') || mimeType.includes('word')) return 'doc';
    if (mimeType.includes('officedocument.wordprocessingml.document')) return 'docx';
    if (mimeType.includes('image')) {
      if (mimeType.includes('jpeg') || mimeType.includes('jpg')) return 'jpg';
      if (mimeType.includes('png')) return 'png';
      return 'image';
    }
  }

  // Priority 3: Filename extension
  if (filename) {
    const ext = filename.split('.').pop()?.toLowerCase();
    const validExtensions = ['pdf', 'doc', 'docx', 'jpg', 'jpeg', 'png'];
    if (validExtensions.includes(ext)) {
      return ext === 'jpeg' ? 'jpg' : ext;
    }
  }

  return 'unknown';
}

for (const item of items) {
  const data = item.json;

  const documentType = data.document_type;
  const filename = data.original_filename || data.fileName || data.filename;
  const mimetype = data.mimeType || data.mimetype;

  const format = detectFormat(documentType, filename, mimetype);

  // Add additional metadata for processing
  const result = {
    json: {
      ...data, // Preserve all original data
      format: format,
      file_extension: filename ? filename.split('.').pop()?.toLowerCase() : null,
      mime_type: mimetype,
      classification_complete: true,
      processing_ready: format !== 'unknown',
      // Ensure filename is preserved
      original_filename: filename || `document_${data.document_id || 'unknown'}`,
      // Add debugging info
      format_detection: {
        input_type: documentType,
        input_filename: filename,
        input_mimetype: mimetype,
        final_format: format
      }
    }
  };

  processedItems.push(result);
}

return processedItems;

