const items = $input.all();
const outputItems = [];

function removeCountryCode(phone) {
  if (typeof phone !== 'string') return phone;
  return phone.replace(/^\+\d{1,3}[-\s]?/, '');
}

function fixDateFormat(dateStr) {
  if (typeof dateStr !== 'string') return dateStr;
  const match = dateStr.match(/^(\d{2})-(\d{2})-(\d{4})$/);
  if (match) {
    return `${match[3]}-${match[2]}-${match[1]}`;
  }
  return dateStr;
}

function cleanPhone(phone) {
  if (typeof phone !== 'string') return '';
  if (phone.trim().toLowerCase() === 'not specified' || phone.trim().toLowerCase() === 'not provided') {
    return '';
  }
  return removeCountryCode(phone);
}

for (const item of items) {
  let rawText = item.json.text;
  rawText = rawText.replace(/\\n/g, '');
  let parsed;
  try {
    parsed = JSON.parse(rawText);
  } catch (e) {
    try {
      parsed = JSON.parse(rawText.trim());
    } catch (err) {
      parsed = { output: {} };
    }
  }
  if (parsed && parsed.output) {
    if (Array.isArray(parsed.output.skills)) {
      parsed.output.skills = parsed.output.skills.join(', ');
    }
    // Clean and validate phone fields
    parsed.output.mobile_number_1 = cleanPhone(parsed.output.mobile_number_1);
    parsed.output.mobile_number_2 = cleanPhone(parsed.output.mobile_number_2);
    parsed.output.phone_number = cleanPhone(parsed.output.phone_number);
    parsed.output.telephone_number = cleanPhone(parsed.output.telephone_number);

    // Fix date fields
    parsed.output.date_of_birth = fixDateFormat(parsed.output.date_of_birth);
    parsed.output.passport_issue_date = fixDateFormat(parsed.output.passport_issue_date);
    parsed.output.passport_expiry_date = fixDateFormat(parsed.output.passport_expiry_date);
    parsed.output.driving_license_issue_date = fixDateFormat(parsed.output.driving_license_issue_date);
    parsed.output.driving_license_expiry_date = fixDateFormat(parsed.output.driving_license_expiry_date);

    outputItems.push({ json: parsed.output });
  } else {
    outputItems.push({ json: {} });
  }
}

return outputItems;

// Combine applicant data and designation names into a single object and assign all data to a variable

const input = $input.all().map(item => item.json);

// Find the applicant object (has more than just designation_name)
const applicant = input.find(obj => !obj.designation_name);

// Collect all designation names into an array
const designations = input
  .filter(obj => obj.designation_name)
  .map(obj => obj.designation_name);

// Add the designations array to the applicant object
let result;
if (applicant) {
  applicant.designation_list = designations;
  result = applicant;
} else {
  result = { designation_list: designations };
}

// Assign all data to a variable 'text' for LLM
const text = JSON.stringify(result);

return [{ json: { ...result, text } }];

// Check designation scores and route accordingly
const items = $input.all();
const results = [];

for (const item of items) {
  const scores = item.json.output;
  let bestDesignation = null;
  let bestScore = 0;

  // Find the highest scoring designation
  for (const [designation, score] of Object.entries(scores)) {
    const numScore = parseInt(score);
    if (numScore > bestScore) {
      bestScore = numScore;
      bestDesignation = designation;
    }
  }

  // If best score is above 80, use it
  if (bestScore > 80) {
    results.push({
      json: {
        ...item.json,
        selected_designation: bestDesignation,
        designation_score: bestScore,
        designation_source: 'ai_match',
        route: 'direct_create'
      }
    });
  } else {
    // If no score above 80, route to AI assignment
    results.push({
      json: {
        ...item.json,
        best_designation: bestDesignation,
        best_score: bestScore,
        route: 'ai_assign'
      }
    });
  }
}

return results;

// Process AI designation assignment result
const items = $input.all();
const results = [];

for (const item of items) {
