// Merge the two input items: one with personal info, one with designation info
const items = $input.all();
if (items.length < 2) {
  // Fallback: just return the first item if only one present
  return items;
}

// Find the personal info object (has applicant_name) and the designation object (has selected_designation)
const personalInfo = items.find(item => item.json.applicant_name);
const designationInfo = items.find(item => item.json.selected_designation);

// Merge all fields, with designation fields overwriting if needed
const merged = {
  ...personalInfo.json,
  ...designationInfo.json
};

// Check if the selected designation exists in the designation list
// Use case-insensitive comparison and trim whitespace for better matching
const designationExists = merged.designation_list && 
  merged.designation_list.some(designation => 
    designation.toLowerCase().trim() === merged.selected_designation.toLowerCase().trim()
  );

// Add routing information
merged.designation_exists = designationExists;
merged.route_action = designationExists ? 'create_applicant' : 'create_designation_first';

// Add debugging information for troubleshooting
merged.debug_info = {
  selected_designation: merged.selected_designation,
  selected_normalized: merged.selected_designation ? merged.selected_designation.toLowerCase().trim() : null,
  designation_list_count: merged.designation_list ? merged.designation_list.length : 0,
  designation_exists: designationExists,
  matching_designations: merged.designation_list ? 
    merged.designation_list.filter(d => 
      d.toLowerCase().trim() === merged.selected_designation.toLowerCase().trim()
    ) : []
};

return [{ json: merged }];

// After attempting to create designation, prepare data for job applicant creation
const items = $input.all();
const result = [];

for (const item of items) {
  // Check if the designation creation was successful or failed
  const hasError = item.json.error || item.json.errorMessage;
  const isSuccess = !hasError && item.json.name; // ERPNext returns 'name' field on successful creation

  // Handle both success and error cases (e.g., duplicate designation)
  let designationStatus = 'unknown';
  let designationMessage = '';

  if (isSuccess) {
    designationStatus = 'created';
    designationMessage = `Designation '${item.json.designation_name || item.json.selected_designation}' created successfully`;
  } else if (hasError) {
    // Check if it's a duplicate error
    const errorMsg = item.json.errorMessage || item.json.error || '';
    if (errorMsg.toLowerCase().includes('duplicate') || 
        errorMsg.toLowerCase().includes('already exists') ||
        errorMsg.toLowerCase().includes('unique')) {
      designationStatus = 'already_exists';
      designationMessage = `Designation '${item.json.selected_designation}' already exists in system`;
    } else {
      designationStatus = 'creation_failed';
      designationMessage = `Failed to create designation: ${errorMsg}`;
    }
  }

  result.push({
    json: {
      ...item.json,
      designation_creation_status: designationStatus,
      designation_creation_message: designationMessage,
      designation_available: isSuccess || designationStatus === 'already_exists',
      ready_for_applicant_creation: true,
      // Clear any error fields since we've handled them
      error: undefined,
      errorMessage: undefined
    }
  });
}

return result;

// Verify designation creation and prepare final data
const items = $input.all();
const result = [];

for (const item of items) {
  // Add final verification and logging
  const finalData = {
    ...item.json,
    workflow_step: 'designation_verified',
    final_verification: {
      has_selected_designation: !!item.json.selected_designation,
      designation_source: item.json.designation_source,
      creation_status: item.json.designation_creation_status,
      ready_for_applicant: item.json.ready_for_applicant_creation
    }
  };

  result.push({ json: finalData });
}

return result;
