// Function to extract years as integer from strings like "13 years", "5 months", etc.
function extractYearsAsInteger(yearsString) {
  if (!yearsString || typeof yearsString !== 'string') {
    return 0;
  }

  // Extract numbers from the string
  const numbers = yearsString.match(/\d+/g);
  if (!numbers || numbers.length === 0) {
    return 0;
  }

  const value = parseInt(numbers[0]);

  // If the string contains "month" or "months", convert to fraction of year
  if (yearsString.toLowerCase().includes('month')) {
    return Math.round(value / 12 * 10) / 10; // Round to 1 decimal place
  }

  // Otherwise, assume it's years
  return value;
}

