function fixDateFormat(dateStr) {
  if (typeof dateStr !== 'string') return dateStr;
  const match = dateStr.match(/^(\d{2})-(\d{2})-(\d{4})$/);
  if (match) {
    return `${match[3]}-${match[2]}-${match[1]}`;
  }
  return dateStr;
}


for (const item of items) {
  let rawText = item.json.text;
  rawText = rawText.replace(/\\n/g, '');
  let parsed;
  try {
    parsed = JSON.parse(rawText);
  } catch (e) {
    try {
      parsed = JSON.parse(rawText.trim());
    } catch (err) {
      parsed = { output: {} };
    }
  }
  if (parsed && parsed.output) {
    if (Array.isArray(parsed.output.skills)) {
      parsed.output.skills = parsed.output.skills.join(', ');
    }

    // Fix date fields
    parsed.output.date_of_birth = fixDateFormat(parsed.output.date_of_birth);
    parsed.output.passport_issue_date = fixDateFormat(parsed.output.passport_issue_date);
    parsed.output.passport_expiry_date = fixDateFormat(parsed.output.passport_expiry_date);
    parsed.output.driving_license_issue_date = fixDateFormat(parsed.output.driving_license_issue_date);
    parsed.output.driving_license_expiry_date = fixDateFormat(parsed.output.driving_license_expiry_date);

