return [{ json: { ...result, text } }];

// Check designation scores and route accordingly
const items = $input.all();
const results = [];

for (const item of items) {
  const scores = item.json.output;
  let bestDesignation = null;
  let bestScore = 0;

  // Find the highest scoring designation
  for (const [designation, score] of Object.entries(scores)) {
    const numScore = parseInt(score);
    if (numScore > bestScore) {
      bestScore = numScore;
      bestDesignation = designation;
    }
  }

  // If best score is above 80, use it
  if (bestScore > 80) {
    results.push({
      json: {
        ...item.json,
        selected_designation: bestDesignation,
        designation_score: bestScore,
        designation_source: 'ai_match',
        route: 'direct_create'
      }
    });
  } else {
    // If no score above 80, route to AI assignment
    results.push({
      json: {
        ...item.json,
        best_designation: bestDesignation,
        best_score: bestScore,
        route: 'ai_assign'
      }
    });
  }
}

return results;

