if (items.length < 2) {
  // Fallback: just return the first item if only one present
  return items;
}

// Find the personal info object (has applicant_name) and the designation object (has selected_designation)
const personalInfo = items.find(item => item.json.applicant_name);
const designationInfo = items.find(item => item.json.selected_designation);

// Merge all fields, with designation fields overwriting if needed
const merged = {
  ...personalInfo.json,
  ...designationInfo.json
};

// Ensure designation_list is properly preserved from either source
if (!merged.designation_list || merged.designation_list.length === 0) {
  // Try to get designation_list from personal info first, then designation info
  merged.designation_list = personalInfo?.json?.designation_list || designationInfo?.json?.designation_list || [];
}

// Debug: Log what we have for designation matching
console.log('DEBUG - Designation matching:', {
  selected_designation: merged.selected_designation,
  designation_list: merged.designation_list,
  designation_list_length: merged.designation_list ? merged.designation_list.length : 0,
  ai_route_decision: merged.route
});

// Check if AI has already made a routing decision
const aiRouteDecision = merged.route;
const hasAIRouting = aiRouteDecision === 'direct_create' || aiRouteDecision === 'create_designation_first';

// Check if the selected designation exists in the designation list
// Use case-insensitive comparison and trim whitespace for better matching
const designationExists = merged.designation_list && merged.designation_list.length > 0 && 
  merged.designation_list.some(designation => 
    designation.toLowerCase().trim() === merged.selected_designation.toLowerCase().trim()
  );

// Smart routing logic: Respect AI decision when available, fallback to list checking
let finalRouteAction;
let routingReason;

if (hasAIRouting) {
  // Trust AI's routing decision
  if (aiRouteDecision === 'direct_create') {
    finalRouteAction = 'create_applicant';
    routingReason = 'ai_determined_designation_exists';
  } else {
    finalRouteAction = 'create_designation_first';
    routingReason = 'ai_determined_designation_missing';
  }
