  // Fallback to traditional list checking
  finalRouteAction = designationExists ? 'create_applicant' : 'create_designation_first';
  routingReason = designationExists ? 'list_check_found_match' : 'list_check_no_match';
}

// Add routing information
merged.designation_exists = hasAIRouting ? (aiRouteDecision === 'direct_create') : designationExists;
merged.route_action = finalRouteAction;
merged.routing_method = hasAIRouting ? 'ai_decision' : 'list_verification';
merged.routing_reason = routingReason;

