  selected_normalized: merged.selected_designation ? merged.selected_designation.toLowerCase().trim() : null,
  designation_list_count: merged.designation_list ? merged.designation_list.length : 0,
  designation_exists: merged.designation_exists,
  designation_list_sample: merged.designation_list ? merged.designation_list.slice(0, 5) : [],
  matching_designations: merged.designation_list ? 
