// Process experience data into ERPNext child table format
const items = $input.all();
const result = [];

for (const item of items) {
  const data = item.json;
  const experienceText = data.experience || "Not Specified";

  // Initialize experience child table array
  let experienceChildTable = [];

  // Check if experience is already an array (from our previous processing)
  if (Array.isArray(data.experience) && data.experience.length > 0) {
    // Experience is already processed as array, use it directly
    experienceChildTable = data.experience.map((exp, index) => ({
      "doctype": "Experience Certifications",
      "company": exp.company || "Not Specified",
      "designation": exp.designation || "Not Specified", 
      "years_of_experience": exp.years_of_experience || 0,
      "experience_description": exp.experience_description || "No description",
      "from_date": exp.from_date || "2000-01-01",
      "to_date": exp.to_date || "2000-01-01",
      "salary": exp.salary || 0,
      "idx": index + 1,
      "parentfield": "experience",
      "parenttype": "Job Applicant"
    }));

    console.log(`Experience already processed as array with ${experienceChildTable.length} entries`);
  } else if (typeof experienceText === 'string' && experienceText !== "Not Specified" && experienceText.trim() !== "") {
    // Parse experience text into structured format
    try {
      // Split by common delimiters and clean up
      const experienceEntries = experienceText
        .split(/[;,]|\band\b|\bthen\b/i)
        .map(entry => entry.trim())
        .filter(entry => entry.length > 10); // Filter out very short entries

      experienceEntries.forEach((entry, index) => {
        // Extract years/duration pattern
        const yearPattern = /(\d+)\s*(year[s]?|month[s]?|yr[s]?)/i;
        const yearMatch = entry.match(yearPattern);

        // Extract company name pattern (variations: "at Company", "in Company", "with Company")
        const companyPattern = /(?:at|in|with|for)\s+([A-Z][A-Za-z\s&\.,\-]+?)(?:\s*[-]|$|\s+as\s|\s+doing\s)/i;
        const companyMatch = entry.match(companyPattern);

        // Extract job title pattern (variations: "as Title", "working as Title")
        const titlePattern = /(?:as|working as)\s+([A-Za-z\s&\.,\-]+?)(?:\s*at|\s*in|\s*with|\s*for|\s*[-]|$)/i;
        const titleMatch = entry.match(titlePattern);

        // Alternative patterns for different formats
        let duration = "Not Specified";
        let company = "Not Specified";
        let title = "Not Specified";

        // Extract duration
        if (yearMatch) {
          duration = `${yearMatch[1]} ${yearMatch[2].toLowerCase()}`;
        }

        // Extract company
        if (companyMatch) {
          company = companyMatch[1].trim().replace(/[,;]$/, ''); // Remove trailing punctuation
        }

        // Extract job title
        if (titleMatch) {
          title = titleMatch[1].trim().replace(/[,;]$/, ''); // Remove trailing punctuation
        } else {
          // Try to extract title from the beginning of the entry
          const words = entry.split(/\s+/);
          const commonTitles = ['technician', 'engineer', 'manager', 'developer', 'analyst', 'assistant', 'officer', 'supervisor', 'coordinator', 'specialist'];
          for (let i = 0; i < Math.min(5, words.length); i++) {
            if (commonTitles.some(titleWord => words[i].toLowerCase().includes(titleWord))) {
              title = words.slice(0, i + 1).join(' ');
              break;
            }
          }
        }

        // If we couldn't extract company or title, use fallback approach
        if (company === "Not Specified" || title === "Not Specified") {
          // Look for ALL CAPS words (often company names)
          const allCapsPattern = /\b[A-Z]{2,}(?:\s+[A-Z]{2,})*\b/g;
          const allCapsMatches = entry.match(allCapsPattern);

          if (allCapsMatches && company === "Not Specified") {
            company = allCapsMatches[0];
          }

          // If still no title, use a generic extraction
          if (title === "Not Specified") {
            const firstWords = entry.split(/\s+/).slice(0, 3).join(' ');
            if (firstWords.length > 5) {
              title = firstWords;
            }
          }
        }

        // Create experience entry in ERPNext child table format
        const experienceEntry = {
          "doctype": "Experience Certifications",  // Child table doctype
          "company": company,
          "designation": title,
          "years_of_experience": duration,
          "experience_description": entry.slice(0, 200), // Limit description length
          "idx": index + 1,  // Row index for child table
          "parentfield": "experience",  // Parent field name
          "parenttype": "Job Applicant"  // Parent doctype
        };

        experienceChildTable.push(experienceEntry);
      });

      // If no structured entries were created, create a single entry with all text
      if (experienceChildTable.length === 0) {
        experienceChildTable.push({
          "doctype": "Experience Certifications",
          "company": "Various Companies",
          "designation": data.job_title || "Not Specified",
          "years_of_experience": "Not Specified",
          "experience_description": experienceText.slice(0, 200),
          "idx": 1,
          "parentfield": "experience",
          "parenttype": "Job Applicant"
        });
      }
    } catch (error) {
      console.log('Error parsing experience:', error);
      // Fallback to simple entry
      experienceChildTable.push({
        "doctype": "Experience Certifications",
        "company": "Not Specified",
        "designation": data.job_title || "Not Specified",
        "years_of_experience": "Not Specified",
        "experience_description": experienceText.slice(0, 200),
        "idx": 1,
        "parentfield": "experience",
        "parenttype": "Job Applicant"
      });
    }
  } else {
    // No experience data or empty experience
    console.log('No experience data found or data is empty');
    experienceChildTable = [];
  }

  // Add the structured experience data to the result
  result.push({
    json: {
      ...data,
      experience: experienceChildTable,  // ERPNext expects 'experience' field name
      experience_count: experienceChildTable.length,
      experience_processing_debug: {
        original_experience: experienceText,
        parsed_entries: experienceChildTable.length,
        processing_method: "child_table_format"
      }
    }
  });
}

return result;

