const results = [];

for (const item of items) {
  const data = item.json;

  // Final safety check before creating designation
  if (data.designation_exists || (data.debug_info && data.debug_info.designation_exists)) {
    // Designation already exists, skip creation
    results.push({
      json: {
        ...data,
        designation_creation_status: 'already_exists',
        designation_creation_message: `Designation '${data.selected_designation}' already exists in system`,
        designation_available: true,
        ready_for_applicant_creation: true,
        skip_creation: true
      }
    });
  } else {
    // Proceed with creation
    results.push({
      json: {
        ...data,
        proceed_with_creation: true
      }
    });
  }
}

return results;

// Combine file data and fieldnames, ensure binary data is preserved
const items = $input.all();
const fileData = items[0];
const fieldnamesData = items[1];

// Preserve binary data from the file input
const result = {
  json: {
    ...fileData.json,
    ...fieldnamesData.json
  },
  binary: fileData.binary || {} // Ensure binary data is preserved
};

return [result];

// Prepare data for Resume Parser with extracted image
const items = $input.all();
const result = [];

for (const item of items) {
  // Use extracted image if available, otherwise use original file
  const binaryData = item.binary.extracted_image || item.binary.file;

  result.push({
    json: {
      ...item.json,
      image_extracted: !!item.binary.extracted_image,
      processing_method: item.binary.extracted_image ? 'vision_analysis' : 'direct_file'
    },
    binary: {
      file: binaryData
    }
  });
}

return result;

// Prepare text data for Resume Parser when image extraction fails
const items = $input.all();
const result = [];

for (const item of items) {
  result.push({
    json: {
      ...item.json,
      processing_method: 'text_analysis',
      extracted_text: item.json.text_content || 'No text extracted',
      fallback_reason: 'Image extraction failed or unavailable'
    },
    binary: item.binary || {}
  });
}

return result;
