const validItems = [];

$input.all().forEach((item, index) => {
  try {
    // Validate basic structure
    if (!item?.binary?.file?.data) {
      console.log(`Skipping item ${index}: Missing binary data`);
      return;
    }

    // Ensure binary data is properly formatted
    const binary = {
      file: {
        data: item.binary.file.data,
        mimeType: item.binary.file.mimeType || 'image/png',
        fileName: item.binary.file.fileName || `image_${index}.png`,
        fileExtension: item.binary.file.fileName?.split('.').pop() || 'png'
      }
    };

    // Validate data content
    if (binary.file.data.length === 0) {
      console.log(`Skipping item ${index}: Empty binary data`);
      return;
    }

    // Ensure the data is in base64 format
    if (Buffer.isBuffer(binary.file.data)) {
      binary.file.data = binary.file.data.toString('base64');
    }

    validItems.push({
      json: {
        fileName: binary.file.fileName,
        mimeType: binary.file.mimeType,
        validation_passed: true,
        timestamp: new Date().toISOString()
      },
      binary
    });

    console.log(`Item ${index} passed validation - file: ${binary.file.fileName}`);
  } catch (error) {
    console.error(`Error processing item ${index}:`, error.message);
  }
});

console.log(`Filter processed ${$input.all().length} items, returning ${validItems.length} valid items`);
