const items = $input.all();
const outputItems = [];

function removeCountryCode(phone) {
  if (typeof phone !== 'string') return phone;
  // Remove country code patterns like +92-, +1-, etc.
  return phone.replace(/^\+\d{1,3}[-\s]?/, '');
}

for (const item of items) {
  let rawText = item.json.text;
  // Remove \n and extra spaces
  rawText = rawText.replace(/\\n/g, '');
  let parsed;
  try {
    parsed = JSON.parse(rawText);
  } catch (e) {
    try {
      parsed = JSON.parse(rawText.trim());
    } catch (err) {
      parsed = { output: {} };
    }
  }
  if (parsed && parsed.output) {
    // Convert skills array to comma-separated string if present
    if (Array.isArray(parsed.output.skills)) {
      parsed.output.skills = parsed.output.skills.join(', ');
    }
    // Remove country code from mobile numbers
    if (parsed.output.mobile_number_1) {
      parsed.output.mobile_number_1 = removeCountryCode(parsed.output.mobile_number_1);
    }
    if (parsed.output.mobile_number_2) {
      parsed.output.mobile_number_2 = removeCountryCode(parsed.output.mobile_number_2);
    }
    if (parsed.output.phone_number) {
      parsed.output.phone_number = removeCountryCode(parsed.output.phone_number);
    }
    outputItems.push({ json: parsed.output });
  } else {
    outputItems.push({ json: {} });
  }
}

return outputItems;
